mapper.insert:
INSERT INTO championship (
	name,
	organizer_id,
	date
)
VALUES (
	#{name, jdbcType=VARCHAR},
	#{organizerId, jdbcType=INTEGER},
	#{date, jdbcType=VARCHAR}
)

mapper.select:
SELECT
	id
FROM
	championship
WHERE
	name = #{name, jdbcType=VARCHAR}
	AND date = #{date, jdbcType=VARCHAR}
	AND organizer_id = #{organizerId, jdbcType=INTEGER}

mapper.update:
UPDATE
	championship
SET
	name = #{name, jdbcType=VARCHAR},
	date = #{date, jdbcType=VARCHAR}
WHERE
	id = #{id, jdbcType=INTEGER}
AND
	organizer_id = #{organizerId, jdbcType=INTEGER}

mapper.delete:
DELETE FROM
	championship
WHERE
	organizer_id = #{organizerId, jdbcType=INTEGER}
	AND id = #{id, jdbcType=INTEGER}

DELETE FROM
	championship
WHERE
	organizer_id = #{organizerId, jdbcType=INTEGER}
	AND id = #{id, jdbcType=INTEGER}
